int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);
	int n;
	cin >> n;
	if (24 <= n) {
		cout << 0 << endl;
		return 0;
	}
	int a[n];
	for (int &i : a) {
		cin >> i;
	}
	int ans = 0;
	for (int i = 0; i < (1 << n); i++) {
		int mini = numeric_limits<int>::max();
		bool puni[24]{};
		for (int j = 0; j < n; j++) {
			if ((i >> j) & 1) {
				if (puni[a[j]]) {
					goto next;
				} else {
					puni[a[j]] = true;
				}
			} else {
				int aa = (24 - a[j]) % 24;
				if (puni[aa]) {
					goto next;
				} else {
					puni[aa] = true;
				}
			}
		}
		if (puni[0]) {
			goto next;
		}
		puni[0] = true;
		for (int i = 0; i < 24; i++) {
			for (int j = 0; j < 24; j++) {
				if (i == j) {
					continue;
				}
				if (puni[i] && puni[j]) {
					mini = min({mini, abs(i - j), 24 - abs(i - j)});
				}
			}
		}
		ans = max(ans, mini);
		next:;
	}
	cout << ans << endl;
	return 0;
}

