		fixed.sort();
		int m = INT32_MAX;
		int a = fixed.front();
		int n = fixed.size();
		rep(i, n - 1) {
			fixed.pop_front();
			int b = fixed.front();
			m = min(m, b - a);
			a = b;
		}
