int dfs(int index,vector<int> &cnt,vector<int> &time){
    if(index==13){
        int pre=-13;
        int res=13;
        for(int i=0;i<time.size()*2;i++){
            if(time[i%24]){
                res=min(res,i-pre);
                pre=i;
            }
        }
        return res;
    }
    if(cnt[index]==0){
        return dfs(index+1,cnt,time);
    }
    if(cnt[index]==2){
        time[(12+index)%24]=true;
        time[12-index]=true;
        int res=dfs(index+1,cnt,time);
        time[(12+index)%24]=false;
        time[12-index]=false;;
        return res;
    }
    time[(12+index)%24]=true;
    int res=dfs(index+1,cnt,time);
    time[(12+index)%24]=false;
    time[12-index]=true;
    res=max(res,dfs(index+1,cnt,time));
    time[12-index]=false;
    return res;
}

