				V.push_back(j+1);
				V.push_back(24-(j+1));
			}
			diff=min(diff,min((j+1),24-(j+1)));
		}
		if(cnt[12]==1){
			V.push_back(12);
			diff=min(diff,12);
		}
		sort(V.begin(),V.end());
		for(int j=0;j<N-1;j++){
			diff=min(diff,abs(V[j]-V[j+1]));
		}
		diff=min(diff,abs(V[0]-V[N-1]));
		ans=max(ans,diff);
	}
	cout<<ans<<endl;
	return 0;
}

