vector<int> dsu;

void init(int n) {
    dsu.resize(n);
    for (int i= 0; i < n; ++i) {
        dsu[i] = i;
    }
}

int find_set(int v) {
    if (dsu[v] == v) {
        return v;
    }
    return dsu[v] = find_set(dsu[v]);
}

void merge(int q, int w) {
    q = find_set(q);
    w = find_set(w);
    dsu[w] = q;
}

void solve(bool read) {
    int n;
    cin >> n;
    ++n;
    vector<int> d(n, 0);
    for (int i = 0; i < n; ++i) {
        cin >> d[i + 1];
    }
    for (int s = 0; s < 12; ++s) {
        init(n);
        for (int i = 0; i < n; ++i) {
            for (int j = i + 1; j < n; ++j) {
                int diff_dist = min(d[i] + d[j], 24 - d[i] - d[j]);
                if (diff_dist < s) {
                    merge(i, j);
                }
            }
        }
        vector<vector<int>> g(n);
        bool flag = true;
        for (int i = 0; i < n; ++i) {
            for (int j = i + 1; j < n; ++j) {
                int same_dist = abs(d[i] - d[j]);
                if (same_dist < s) {
                    int from = find_set(i);
                    int to = find_set(j);
                    if (from == to) {
                        flag = false;
                        break;
                    }
                    g[find_set(i)].push_back(find_set(j));
                    g[find_set(j)].push_back(find_set(i));
                }
            }
        }
        vector<int> col(n, -1);
        for (int i = 0; i < n; ++i) {
            if (col[i] == -1) {
                col[i] = 0;
                queue<int> q;
                q.push(i);
                while (!q.empty()) {
                    int v = q.front();
                    q.pop();
                    for (int to : g[v]) {
                        if (col[to] == -1) {
                            col[to] = col[v] ^ 1;
                            q.push(to);
                        } else if (col[to] == col[v]) {
                            flag = false;
                            break;
                        }
                    }
                }
            }
        }
        if (!flag) {
            cout << s - 1 << endl;
            return;
        }
    }
    cout << 11 << endl;
}

