void solve(){
    int N;cin>>N;
    vector<int> D(N+1,0);
    REP(i, N) cin >> D[i];
    int ans=INT_MAX;
    REP(i,N+1) REP(j,N+1){
        if(i==j) continue;
        int tmp=0;
        tmp=abs(D[i]-D[j]);
        if(D[j]>0) tmp=max(tmp,abs(D[i]-(24-D[j])));
        if(D[i]>0) tmp=max(tmp,abs((24-D[i])-D[j]));
        if(D[i]>0&&D[j]>0) tmp=max(tmp,abs((24-D[i])-(24-D[j])));
        ans=min(ans,tmp);
    }
    cout<<ans<<endl;
}

