        return 1;
    }

    int ret = 0;
    for (int i = 0; i < (1 << N); i++) {
        std::vector<int> d(N, 0);
        for (int j = 0; j < N; j++) {
            if (i >> j & 1) {
                d[j] = D[j] % 24;
            } else {
                d[j] = (24 - D[j]) % 24;
            }
        }

        int score = 10000000;
        for (int j = 0; j < N; j++) {
            for (int k = j + 1; k < N; k++) {
