int bit(int k, int x)
{
    return (x >> k & 1);
}

inline int time_gap(int x, int y)
{
    if(x < y) swap(x, y);
    return min(x - y, 24 - x + y);
}

int min_gap(vector<int>&v)
{
    int res = maxn;
    for(int i = 0; i < (int)v.size(); ++i)
        for(int j = i + 1; j < (int)v.size(); ++j)
            res = min(res, time_gap(v[i], v[j]));
    return res;
}

void solve()
{
    for(int i = 0; i <= 12; ++i)
        if(cnt[i] >= 3 || ((i == 0 || i == 12) && cnt[i] >= 2))
        {
            cout << "0\n";
            return;
        }
    for(int i = 1; i < 12; ++i)
    {
        if(cnt[i] == 2)
        {
            vec.push_back(i);
            vec.push_back(24 - i);
        }
        else if(cnt[i] == 1)
            can_rev.push_back(i);
    }
    if(cnt[12] > 0) vec.push_back(12);
    vec.push_back(0);

    int ans = 0;
    for(int mask = 0; mask < (1 << (int)can_rev.size()); ++mask)
    {
        vector<int> all = vec;
        for(int i = 0; i < (int)can_rev.size(); ++i)
            all.push_back(bit(i, mask) ? 24 - can_rev[i] : can_rev[i]);
        ans = max(ans, min_gap(all));
    }
    cout << ans << '\n';
}

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    read_input();
    solve();
}

