i64 N;

i64 diff(i64 x, i64 y) {
    i64 d = abs_diff(x, y);
    return min(d, 24-d);
}

i64 f(vector<i64>& v) {
    ALL(sort, v);

    i64 res = 99999;
    chmin(res, diff(0, v.front()));
    chmin(res, diff(v.back(), 0));

    i64 n = SIZE(v);
    REP(i, n-1) {
        chmin(res, diff(v[i], v[i+1]));
    }

    return res;
}

void solve() {
    vector<i64> va; // 確定
    vector<i64> vb; // 未確定

    array<i64,13> cnt {};
    REP(_, N) {
        i64 d; RD(d);
        ++cnt[d];
    }

    if(cnt[0] > 0) {
        PRINTLN(0);
        return;
    }
    FOR(d, 1, 12) {
        if(cnt[d] >= 3) {
            PRINTLN(0);
            return;
        }
        else if(cnt[d] == 2) {
            va.emplace_back(d);
            va.emplace_back(24-d);
        }
        else if(cnt[d] == 1) {
            vb.emplace_back(d);
        }
    }
    if(cnt[12] >= 2) {
        PRINTLN(0);
        return;
    }
    else if(cnt[12] == 1) {
        va.emplace_back(12);
    }

    i64 ans = -1;
    i64 m = SIZE(vb);
    REP(i, 1<<m) {
        vector<i64> v(va);
        REP(j, m) {
            i64 d = vb[j];
            i64 t = GETBIT(i,j) ? 24-d : d;
            v.emplace_back(t);
        }
        chmax(ans, f(v));
    }

    PRINTLN(ans);
}

signed main(signed /*argc*/, char** /*argv*/) {
    RD(N);



    solve();

    EXIT();
}

