template<class A,class B>bool amin(A&a,const B&b){return b<a?a=b,1:0;}
ll ri(){ll l;cin>>l;return l;} string rs(){string s;cin>>s;return s;}

ll solve() {
	int n = ri();
	vector<int> A(n);
	rep(i, n) A[i] = ri();

	sort(all(A));


	vi C(13);
	forr(a, A) C[a]++;

	vi fix(25);
	vi non;

	if (C[0] > 0) return 0;
	if (C[12] >= 2) return 0;


	rep(i, 13) {
		if (C[i] >= 3) return 0;
		if (C[i] == 2) {
			fix[C[i]] = 1;
			fix[24-C[i]] = 1;
		}
		if (C[i] == 1) {
			non.emplace_back(i);
		}
	}

	out(fix);
	out(non);

	int m = sz(non);

	int ans = 0;

	rep(mask, bit(m)) {
		vi T(fix);
		T[0] = T[24] = 1;

		bool ok = 1;
		rep(j, m) {
			int s = non[j];
			int t = (bit(j) & mask) ? t = s : 24 - s;

			if (T[t]) {
				ok = 0;
				break;
			}
			T[t] = 1;
		}
		out(mask, ok, T);
		if (ok) {
			int cand = 12;
			vi X;
			rep(x, 25) {
				if (T[x]) X.emplace_back(x);
			}
			int k = sz(X);
			rep(j, k - 1) {
				amin(cand, X[j+1] - X[j]);
			}
			amax(ans, cand);
		}
	}

	return ans;
}

void Main() {
	cout << solve() << endl;
}
