int dist(int b){
	int res=INF;

	for (int i=0;i<=12;i++){
		if (b&b<<i){
			res=i;
			break;
		}
	}
	for (int i = 1; i < res; i++){
		if (b&(b>>24-i)){
			res = i;
			break;
		}
	}

	for (int i = 0; i< res; i++){
		if (b&1<<i){
			res = i;
			break;
		}
	}
	for (int i = 1; i < res; i++){
		if (b& 1<< (24 - i)){
