{
    ll n; llin(n);
    V<ll> dlist; llina(dlist, n);

    ll counts[13];
    memset(counts, 0, sizeof(counts));
    counts[0]++;

    for (auto d : dlist) counts[d]++;

    if (counts[0] >= 2LL) {
        cout << 0 << endl;
        return 0;
    }

    srep (i, 1, 13) {
        if (counts[i] >= 3) {
            cout << 0 << endl;
            return 0;
        }
    }

    set<ll> master_timeset;
    master_timeset.insert(12LL);

    V<ll> variables;

    srep (i, 1, 13) {
        if (counts[i] == 2) {
            master_timeset.insert(12LL - i);
            master_timeset.insert(12LL + i);
        }
        if (counts[i] == 1) {
            variables.pb(i);
        }
    }

    ll ans = 0LL;

    rep (bitmap, (ll)pow(2LL, variables.size())) {
        set<ll> timeset = master_timeset;

        rep (i, variables.size()) {
            if (bitmap & (1LL<<i)) timeset.insert(12LL - variables[i]);
            else timeset.insert(12LL + variables[i]);
        }

        ll minsub = 100LL;

        auto iter = timeset.end();
        iter--;
        ll prev = (*iter) - 24LL;
        for (auto tm : timeset) {
            minsub = min(minsub, tm - prev);
            prev = tm;
        }

        ans = max(ans, minsub);
    }

    cout << ans << endl;

    return 0;
}

