vector<LL> d(13,0);
vector<bool> frag(24,false);

LL solve(){
    LL ans = 24;
    for(LL i = 0; i < 24; i++){
        for(LL j = i+1; j < 24; j++){
            if(frag[j] && frag[i]){
                ans = min(ans,min(j-i,24-(j-i)));
            }
        }
    }
    return ans;
}

LL dfs(LL ind){

    if(ind == 13){
        return solve();
    }

    if(d[ind] == 0){
        return dfs(ind+1);
    }else if(d[ind] == 1){
        frag[ind] = true;
        LL ret1 = dfs(ind+1);
        frag[ind] = false; 
        frag[24-ind] = true;
        LL ret2 = dfs(ind+1);
        frag[24-ind] = false;
        return max(ret1,ret2);
    }else{
        frag[ind] = true;
        frag[24-ind] = true;
        LL ret = dfs(ind+1);
        frag[ind] = false;
        frag[24-ind] = false;
        return ret;
    }
}

