using namespace std;

int d[50];
bool dp[51][1<<24];
int main(){
	int n;scanf("%d",&n);
	int Max=0;
	rep(i,n){
		scanf("%d",&d[i]);
	}
	dp[0][0]=true;
	rep(i,n)rep(j,1<<24){
		if(dp[i][j]==false)continue;
		dp[i+1][j|1<<d[i]]=true;
		dp[i+1][j|1<<(24-d[i])]=true;
	}
	rep(i,1<<24){
		if(i==0)continue;
		if(!dp[n][i])continue;
		vector<int>v;
		rep(j,24){
			if(i>>j&1)v.push_back(j);
		}
		if(v.size()>n)continue;
		int sum=INT_MAX;
		rep(i,v.size()){
			sum=min({sum,v[i],24-v[i]});
			if(i<v.size()-1){
				int A=v[i+1]-v[i];
				sum=min({sum,A,24-A});
			}
			else{
				int A=v.back()-v.front();
				sum=min({sum,A,24-A});
			}
		}
		Max=max(Max,sum);
	}
	cout<<Max<<endl;
}

