const double eps = 1e-10;
int n, d[50];
int _tm(int a){
  if(a < 0) a+=24;
  return min(a, 24-a);
}
bool check(int a){
  vpii G[50][2];
  bool z[50][2], zz[50][2];
  Fill(z, false);
  FOR(i, 0, n-1)FOR(j, i+1, n){
    if(_tm(d[i]-d[j]) < a){
      G[i][0].pb(mp(j, 1));
      G[i][1].pb(mp(j, 0));
      G[j][0].pb(mp(i, 1));
      G[j][1].pb(mp(i, 0));
    }
    if(_tm(d[i]+d[j]) < a){
      G[i][0].pb(mp(j, 0));
      G[i][1].pb(mp(j, 1));
      G[j][0].pb(mp(i, 0));
      G[j][1].pb(mp(i, 1));
    }
  }
  queue<pii> q;
  FOR(i, 0, n){
    if(z[i][0]) continue;
    Fill(zz, false);
    q.push(mp(i, 0));
    z[i][0] = true;
    zz[i][0] = true;
    while(sz(q)){
      pii t = q.front();
      q.pop();
      each(itr, G[t.fi][t.se]){
        if(zz[itr->fi][1-itr->se]) return false;
        if(zz[itr->fi][itr->se]) continue;
        q.push(*itr);
        z[itr->fi][itr->se] = true;
        zz[itr->fi][itr->se] = true;
      }
    }
  }
  return true;
}
main(){
  cin.tie(0);
  ios::sync_with_stdio(false);
  cin >> n;
  int minv = iinf;
  FOR(i, 0, n){
    cin >> d[i];
    mins(minv, d[i]);
  }
  int l = 0, r = minv+1, mid;
  while(r - l > 1){
    mid = (l+r)/2;
    if(check(mid)){
      l = mid;
    }else{
      r = mid;
    }
  }
  cout << l << endl;
  return 0;
}

