int min_d(vector<bool> & ex) {
  vector<int> a;
  rep (i, (int)ex.size()) {
    if (ex[i]) {
      a.push_back(i);
    }
  }
  int ret = a.front() + 24 - a.back();
  rep (i, (int)a.size() - 1) {
    ret = min(ret, a[i+1] - a[i]);
  }
  return ret;
