void dfs(int p){
	if(p == bin){
		int tmp = 99;
		for(int i=0; i<24; ++i){
			for(int j=i+1; j<24; ++j){
				if(cnt[i] && cnt[j]){
					tmp = min(tmp, min(abs(i-j), 24-abs(i-j)));
				}
			}
		}
		ans = max(ans, tmp);
		return;
	}
	++cnt[bi[p]]; dfs(p+1); --cnt[bi[p]];
	++cnt[24-bi[p]]; dfs(p+1); --cnt[24-bi[p]];
}

