using namespace std;

int calc_s(int nds[13], int ps[13]) {
  int ret;
  vector<int> ts;
  for (int d = 0; d < 13; d++) {
    if (nds[d] > 0) {
      if (ps[d] == 0) {
        ts.push_back(d);
        ts.push_back(24 - d);
      } else if (ps[d] > 0) {
        ts.push_back(d);
      } else {
        ts.push_back(24 - d);
      }
    }
  }
  sort(ts.begin(), ts.end());
  // for (int d = 0; d < 13; d++) {
  //   printf("d = %d, nd = %d, p = %d\n", d, nds[d], ps[d]);
  // }
  // for (int i = 0; i < ts.size(); i++) {
  //   printf("i = %d, t = %d\n", i, ts[i]);
  // }
  ret = 24 + ts[0] - ts[ts.size() - 1];
  for (int i = 1; i < ts.size(); i++) {
    ret = min(ret, ts[i] - ts[i - 1]);
  }
  // printf("ret = %d\n", ret);
  return ret;
}

int solve(int nds[13], int d, int ps[13]) {
  if (d >= 13) {
    return calc_s(nds, ps);
  }
  if (nds[d] == 0) {
    return solve(nds, d + 1, ps);
  } else if (nds[d] == 1) {
    int ret1, ret2;
    ps[d] = 1;
    ret1 = solve(nds, d + 1, ps);
    ps[d] = -1;
    ret2 = solve(nds, d + 1, ps);
    return max(ret1, ret2);
  } else {
    ps[d] = 0;
    return solve(nds, d + 1, ps);
  }
}

int main(int argc, char *argv[]) {
  // read inputs
  int n, ds[MAX_N + 1];
  scanf("%d", &n);
  for (int i = 0; i < n; i++) {
    scanf("%d", &ds[i]);
  }
  ds[n] = 0;

  // count how many times each value occurs
  int nds[13] = {};
  for (int i = 0; i <= n; i++) {
    nds[ds[i]]++;
  }

  // if a value occurs more than 3 times, the answer is 0
  for (int d = 1; d < 13; d++) {
    if (nds[d] >= 3) {
      printf("0\n");
      return 0;
    }
  }
  if (nds[0] >= 2) {
    printf("0\n");
    return 0;
  }

  // NOTE : from here, each value occurs at most 2 times
  //        and the answer is not 0
  int ps[13] = {};
  printf("%d\n", solve(nds, 0, ps));

  return 0;
}

