    double distance_ls_p(Point a, Point b, Point c) {
      if ( dot(b-a, c-a) < EPS ) return (c-a).absv();
      if ( dot(a-b, c-b) < EPS ) return (c-b).absv();
      return abs(cross(b-a, c-a)) / (b-a).absv();
