void solve(long long N, std::vector<long long> D) {
  sort(all(D));

  map<ll, ll> dCnts;
  rep(i, N) { dCnts[D[i]]++; }

  vl fixedD;

  bool right = true;
  for (auto iter = dCnts.begin(); iter != dCnts.end(); iter++) {
    ll d = iter->first;
    ll cnt = iter->second;

    if (cnt == 1) {
      if (right) {
        fixedD.push_back(d);
        right = false;
      } else {
        fixedD.push_back(24 - d);
        right = true;
      }
    } else if (cnt == 2) {
      fixedD.push_back(d);
      fixedD.push_back(24 - d);
    } else {
      print(0);
      return;
    }
  }

  sort(all(fixedD));
  ll ans = 1e10;
  ll pre = 0;
  for (ll i = 0; i < fixedD.size(); i++) {
    ans = min(ans, fixedD[i] - pre);
    pre = fixedD[i];
  }
  ans = min(ans, 24 - fixedD[fixedD.size() - 1]);
  print(ans);
}

