int count() {

	int mini = INF;

	for (int i = 0; i < 24; i++) {
		for (int j = 0; j < 24; j++)
		{
			if (i == j)
			{
				continue;
			}

			if (tm[i] == 0 || tm[j] == 0)
			{
				//この時間に両方ない場合
				continue;
			}
			int d1 = abs(i - j);
			int d2 = 24 - d1;
			mini = min(mini, min(d1, d2));
		}
	}
	return mini;
}


int solve() {

	if (cnt[0] > 0) {
		//自分以外にもう1人存在
		return 0;
	}
	tm[0] = 1;

	if (cnt[12] >= 2)
	{
		//12時は１通りしか置き方がないので2人いるときは差は0
		return 0;
	}
	else if (cnt[12] == 1)
	{
		tm[12] = 1;
	}


	vector<int> v;//確定していない候補

	//[1,11]の範囲
	for (int i = 1; i < 12; i++) 
	{
		if (cnt[i] > 2)
		{
			//同じ時間に2人いるケース
			return 0;
		}
		else if (cnt[i] == 2)
		{
			//配置の仕方は一意に決まるケース
			tm[i] = tm[24 - i] = 1;
		}
		else if (cnt[i] == 1)
		{
			//2通りあるので調べる候補
			v.push_back(i);
		}
	}

	int siz = v.size();
	if (siz == 0)
	{
		//調べる候補がなかった場合
		return count();
	}

	int ans = 0;
	//まだ確定していないものがあるので全探索
	for (int i = 0; i < (1 << siz); i++)
	{
		for (int j = 0; j < siz; j++)
		{
			//2通り調べるのでbitを見てどちらに置くか決める
			if ((i >> j) & 1)
			{
				//午前
				tm[v[j]] = 1;
				tm[24 - v[j]] = 0;
			}
			else
			{
				//午後
				tm[v[j]] = 0;
				tm[24 - v[j]] = 1;
			}
		}
		//最小値の最大化
		ans = max(ans, count());

	}
	return ans;

}




