int minTime(vector<int> &arr) {
    int time = 24;
    vector<int> c;
    for (int i = 0; i < arr.size(); i++) {
        if (arr[i] >= 1) c.push_back(i);
    }
    for (int i = 0; i < c.size() - 1; i++) time = min(time, c[i + 1] - c[i]);
    time = min(time, 24 - c.back());
    return time;
}

