typedef ::std::int_fast64_t i64;
typedef ::std::uint_fast64_t u64;
typedef ::std::int_fast32_t i32;
typedef ::std::uint_fast32_t u32;

namespace tools {
  template <typename T>
  void read(::std::istream& is, ::std::vector<T>& vector, const typename ::std::vector<T>::size_type size) {
    vector.reserve(size);
    ::std::copy_n(::std::istream_iterator<T>(is), size, ::std::back_inserter(vector));
  }
  template <typename T>
  void read(::std::vector<T>& vector, const typename ::std::vector<T>::size_type size) {
    ::tools::read(::std::cin, vector, size);
  }

  template <typename T, ::std::size_t N>
  void read(::std::istream& is, ::std::array<T, N>& array) {
    ::std::copy_n(std::istream_iterator<T>(is), N, array.begin());
  }
  template <typename T, ::std::size_t N>
  void read(::std::array<T, N>& array) {
    ::tools::read(::std::cin, array);
  }
}

#endif

// main.cpp

int main() {
  i64 N;
  std::cin >> N;
  std::vector<i64> D;
  tools::read(D, N);

  std::array<i64, 13> occurrences;
  std::fill(occurrences.begin(), occurrences.end(), 0);
  for (const i64& D_i : D) {
    ++occurrences[D_i];
  }

  if (occurrences[0] >= 1 || occurrences[12] >= 2 || std::any_of(occurrences.begin() + 1, occurrences.begin() + 12, [](const i64& occurrence) {
    return occurrence >= 3;
  })) {
    std::cout << 0 << std::endl;
    return 0;
  }

  std::set<i64> time({0});
  if (occurrences[12] == 1) time.insert(12);
  for (i64 i = 1; i <= 11; ++i) {
    if (occurrences[i] == 2) {
      time.insert(i);
      time.insert(24 - i);
    }
  }

  std::vector<i64> unique;
  for (i64 i = 1; i <= 11; ++i) {
    if (occurrences[i] == 1) {
      unique.push_back(i);
    }
  }

  i64 answer = std::numeric_limits<i64>::min();
  for (i64 state = 0; state < tools::pow2<i64>(unique.size()); ++state) {
    std::set<i64> buffer = time;
    for (i64 i = 0; i < static_cast<i64>(unique.size()); ++i) {
      buffer.insert(((state >> i) & 1) == 0 ? unique[i] : 24 - unique[i]);
    }
    i64 possible_answer = 24 - *buffer.rbegin();
    for (auto prev_it = buffer.begin(), it = ++buffer.begin(); it != buffer.end(); prev_it = it, ++it) {
      possible_answer = std::min(possible_answer, *it - *prev_it);
    }
    answer = std::max(answer, possible_answer);
  }
  std::cout << answer << std::endl;
  return 0;
}

