void DFS(int i)
{
	if (i == 13)
	{
		int cur = 100;
		for (int a = 0; a < 24; a ++)
		{
			if (used[a])
			{
				for (int b = 1; b <= 24; b ++)
					if (used[(a + b) % 24] || used[(a - b + 24) % 24])
					{
						cur = min(cur, b);
						break;
					}
			}
		}
		if (cur > ans) ans = cur;
		return;
	}
	if (cnt[i] == 0)
		DFS(i + 1);
	else if (cnt[i] == 2)
	{
		used[i] = used[24 - i] = 1;
		DFS(i + 1);
		used[i] = used[24 - i] = 0;
	}
	else
	{
		used[i] = 1;
		DFS(i + 1);
		used[i] = 0;
		if (i != 0)
		{
			used[24 - i] = 1;
			DFS(i + 1);
			used[24 - i] = 0;
		}
	}
}

