#define VERA(i,a,b) for(ll i=ll(a);i<ll(b);++i)
#define ANDY(i,a,b) for(ll i=ll(a);i>ll(b);--i)
#define SIZE(x) (ll)(x.size())

typedef long long ll;
typedef double dd;

ll N;
vector<ll> D;

ll res;
vector<bool> was;

void Rec(ll per, ll low) {
  if (low <= res) return;
  if (per == N) {
    res=max(res,low);
    return;
  }
  const ll lef = D[per], rig = (24-D[per]) % 24;
  if (!was[lef]) {
    ll gap=24;
    VERA(k,0,was.size())
      if (was[k])
        gap=min(gap,min(abs(k-lef), 24-abs(k-lef)));
    was[lef]=true;
    Rec(per+1,min(low,gap));
    was[lef]=false;
  }
  if (!was[rig]) {
    ll gap=24;
    VERA(k,0,was.size())
      if (was[k])
        gap=min(gap,min(abs(k-rig), 24-abs(k-rig)));
    was[rig]=true;
    Rec(per+1,min(low,gap));
    was[rig]=false;
  }
  if (was[lef] && was[rig]) {
    res=0;
    return;
  }
}

void Solve() {
  sort(D.begin(), D.end());
  res=0;
  was.resize(24,false);
  was[0]=true;
  Rec(0,D.front());
  cout << res << endl;
}

bool Input() {
  if (!(cin>>N)) return false;
  D.resize(N);
  for(auto& d : D) cin >> d;
  return true;
}

}

int main() {
  ios::sync_with_stdio(false);
  cin.tie(nullptr);
  while (Input()) {
    Solve();
  }
  return 0;
}

