	vector<int> cmp,//cmp[i]:頂点iが属する強連結成分の番号
		visited,
		num;//帰りがけ順で頂点が入る
	Scc(int N) {
		n = N;
		cmp.resize(n);
		visited.resize(n);
		g.resize(n);
		rg.resize(n);
	}
	void add_edge(int from, int to) {
		g[from].push_back(to);
		rg[to].push_back(from);
	}

	void dfs(int v) {
		visited[v] = true;
		for (int i = 0; i < g[v].size(); ++i) {
			if (!visited[g[v][i]])dfs(g[v][i]);
		}
		num.push_back(v);
	}

	void rdfs(int v, int k) {
		visited[v] = true;
		cmp[v] = k;
		for (int i = 0; i < rg[v].size(); ++i) {
			if (!visited[rg[v][i]])rdfs(rg[v][i], k);
		}
	}
	int calc() {
		for (int i = 0; i < n; ++i)visited[i] = false;
		num.clear();
		for (int i = 0; i < n; ++i) {
			if (!visited[i])dfs(i);
		}
		for (int i = 0; i < n; ++i)visited[i] = false;
		int k = 0;
		for (int i = num.size() - 1; i >= 0; --i) {
			if (!visited[num[i]]) {
				rdfs(num[i], k);
				++k;
			}
		}
		return k;
	}
};

int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);
	int n;
	cin >> n;
	vi d(n);
	REP(i, n)cin >> d[i];
	int mint = INF;
	REP(i, n) {
		mint = min(min(d[i], 24 - d[i]), mint);
	}
	RREP(t, mint + 1) {
		Scc scc(2 * n);
		int f = 1;
		REP(i, n) {
			FOR(j, i + 1, n) {
				if (abs(d[i] - d[j]) < t) {
					if (abs(d[i] + d[j] - 24) < t) {
						goto next;
					}
					else {
						scc.add_edge(i, n + j);
						scc.add_edge(j, n + i);
					}
				}
				if (min(abs(d[i] + d[j] - 24),d[i]+d[j]) < t) {
					scc.add_edge(i, j);
					scc.add_edge(j, i);
				}
			}
		}
		scc.calc();

		REP(i, n) {
			if (scc.cmp[i] == scc.cmp[n + i])f = 0;
		}
		if (f) {
			cout << t << endl;
			return 0;
		}
	next:;
	}
}

