struct UF{
	int data[51 * 2];
	int rk[51 * 2];
	int n;
	void init(int n){
		this->n = n;
		REP(i, n) {
			data[i] = i;
			rk[i] = 0;
		}
	}
	bool find(int i){
		return i==data[i] ? i : data[i] = find(data[i]);
	}
	bool same(int a, int b){
		return find(a) == find(b);
	}
	void unite(int a, int b){
		a = find(a);
		b = find(b);
		if(a==b)return;
		if(rk[a] < rk[b]){
			data[a] = b;
		}else{
			data[b] = a;
			if(rk[a] == rk[b]) rk[b]++;
		}
	}
};

