void Main()
{
	int N;
	std::cin >> N;
	std::vector<int> flags(1 << 24);
	flags[0] = true;
	int counts[13] = {};
	++counts[0];
	for (int i = 0; i < N; ++i)
	{
		int D;
		std::cin >> D;
		++counts[D];
	}
	std::vector<int> target;
	if (counts[0])
	{
		target.emplace_back(0);
	}
	for (int i = 1; i < 12; ++i)
	{
		for (int j = 0; j < std::min(3, counts[i]); ++j)
		{
			target.emplace_back(i);
		}
	}
	if (counts[12])
	{
		target.emplace_back(12);
	}
	for (auto const& D : target)
	{
		std::vector<int> nexts(1 << 24);
		auto a = 1 << (12 - D);
		auto b = 1 << (12 + D);
		if (D == 12 || D == 0)
		{
			for (int i = (1 << 24) - 1; i >= 0; --i)
			{
				if (i&a)
				{
					nexts[i | a] |= std::min(3 * flags[i], 3);
				}
				else
				{
					nexts[i | a] |= flags[i];
				}
			}
		}
		else
		{
			for (int i = (1 << 24) - 1; i >= 0; --i)
			{
				if (i&a)
				{
					nexts[i | a] |= std::min(3 * flags[i], 3);
				}
				else
				{
					nexts[i | a] |= flags[i];
				}
				if (i&b)
				{
					nexts[i | b] |= std::min(3 * flags[i], 3);
				}
				else
				{
					nexts[i | b] |= flags[i];
				}
			}
		}
		std::swap(nexts, flags);
	}
	int max = -1;
	for (int i = 0; i < (1 << 24); ++i)
	{
		if (flags[i] == 1)
		{
			int min = 24;
			int init = 0;
			for (; init < 24; ++init)
			{
				if ((1 << init)&i)
				{
					break;
				}
			}
			if (init == 24)
			{
				continue;
			}
			int prev = init;
			for (int j = prev + 1; j < 24; ++j)
			{
				if ((1 << j)&i)
				{
					min = std::min(min, j - prev);
					prev = j;
				}
			}
			min = std::min(24 - prev + init, min);
			max = std::max(min, max);
		}
		else
		{
			max = std::max(max, 0);
		}
	}
	std::cout << max << std::endl;
}

