void zipp() {
    map<int, int> zip;
    repeat(i, n) {
        zip[dd[i]]++;
    }

    int m = 0;
    for (auto p : zip) {
        if (p.second >= 3) // 3人が同じ時刻の差であるとき，必ず2人は同じ時差に含まれるはず，よってどの組み合わせにおいても差は必ず0
            bye("0");
        repeat(i, min(2, p.second))
            dd[m++] = p.first;
    }

    n = m;
}


void guchoku() {
    int ww[30];
    int best = 0;
    ww[0] = ww[24] = 1;
    for (int bit = 0; bit < 1 << n; ++bit) {
        bool f = false;
        int b = bit;
        fill(ww+1, ww + 24, 0);
        repeat(i, n) {
            int& w = ww[(b & 1) ? dd[i] : 24 - dd[i]];
            if (w) f=true;
            w = 1;
            b >>= 1;
        }
        if (f) continue;
        //debuga(ww,26);
        int nice = 25;
        int last = 0;
        iterate(i, 1, 25) {
            if (!ww[i]) continue;
            minset(nice, int(i)-last);
            last = i;
        }
        maxset(best, nice);
    }
    cout << best << endl;
}

