        }
    }

    int ans = -1e8;
    for (int f=0; f<(1<<n); ++f) {
        int tmp = 1e8;
        rep(i, n) {
            rep(j, n) {
                if (i == j) continue;
                tmp = min(tmp, abs( dp[ f&(1<<i) ][i] - dp[ f&(1<<j) ][j] ));
            }
        }
        ans = max(ans, tmp);
    }
    cout << ans << endl;
}

