    for(int num:se){
      if(bit&(1<<cnt)){
	hog.pb((24-num)%24);
      }else{
	hog.pb(num);
      }
      cnt++;
    }
    int mi=INF;
    for(int i=0;i<hog.size();i++)
      for(int j=i+1;j<hog.size();j++){
	if(hog[i]==0){
	  mi=min(mi,min(24-hog[j],hog[j]));
	}else if(hog[j]==0){
	  mi=min(mi,min(24-hog[i],hog[i]));
	}else
	  mi=min(mi,abs(hog[i]-hog[j]));
      }
    for(int i=0;i<dep;i++)hog.pop_back();
    ans=max(ans,mi);
    return;
  }

  dfs(bit+1<<d,d+1);
  dfs(bit,d+1);
}
