int n, d[N], x[N][2], g[N << 1][N << 1];

int dis(int x, int y)
{
    int d = std::abs(x - y);
    return std::min(d, 24 - d);
}

bool check(int ans)
{
    memset(g, 0, sizeof(g));
    for (int i = 0; i < n; ++ i) {
        for (int j = 0; j < n; ++ j) {
            if (i == j) {
                continue;
            }
            for (int p = 0; p < 2; ++ p) {
                for (int q = 0; q < 2; ++ q) {
                    if (dis(x[i][p], x[j][q]) < ans) {
                        g[i << 1 | p][j << 1 | q ^ 1] = true;
                    }
                }
            }
        }
    }
    int n2 = n << 1;
    for (int k = 0; k < n2; ++ k) {
        for (int i = 0; i < n2; ++ i) {
            for (int j = 0; j < n2; ++ j) {
                g[i][j] |= g[i][k] && g[k][j];
            }
        }
    }
    for (int i = 0; i < n; ++ i) {
        int i0 = i << 1;
        int i1 = i << 1 | 1;
        if (g[i0][i1] && g[i1][i0]) {
            return false;
        }
    }
    return true;
}

