             r[i] = q[i] ;
         sort( r + 1 , r + 1 + cnt ) ;
         int minx = 10086 ;
         for( int i = 1 ; i <= cnt - 1 ; i++ )
             minx = min( minx , r[i + 1] - r[i] ) ;
         minx = min( minx , 24 - r[cnt] + r[1] ) ;
         ans = max ( ans , minx ) ;
