     if ( b[k] == 0 ) dfs( k + 1 );
     if ( b[k] == 1 ){
         cnt++ ; q[cnt] = k ; dfs( k + 1 ) ; cnt-- ;
         cnt++ ; q[cnt] = 24 - k ; dfs( k + 1 ) ; cnt-- ;
     }
     if ( b[k] == 2 ){
         cnt++ ; q[cnt] = k ; cnt++ ;
         q[cnt] = k ; dfs( k + 1 ) ; cnt -= 2 ;
         cnt++ ; q[cnt] = 24 - k ; cnt++ ;
         q[cnt] = k ; dfs( k + 1 ) ; cnt -= 2 ;
         cnt++ ; q[cnt] = 24 - k ; cnt++ ;
         q[cnt] = 24 - k ; dfs( k + 1 ) ; cnt -= 2 ;
     }
}
