#define SZ(vec)         int((vec).size())
#define ALL(data)       data.begin(),data.end()
#define RALL(data)      data.rbegin(),data.rend()
#define TYPEMAX(type)   std::numeric_limits<type>::max()
#define TYPEMIN(type)   std::numeric_limits<type>::min()

#define pb push_back
#define eb emplace_back

int solve(vector<int>& a) {
    a[0] += 1;
    if (*std::max_element(ALL(a)) >= 3)
        return 0;

    vector<int> sttic;
    int MSK = 0;
    for (int i = 0; i <= 12; ++i)
        if (a[i] == 2) {
            sttic.push_back(i);
            sttic.push_back((24 - i) % 24);
        } else if (a[i] == 1) {
            MSK |= (1 << i);
        }

    int bst = -1;
    for (int i = 0; i != (1 << 13); ++i)
        if ((MSK | i) == MSK) {
            vector<int> lst = sttic;

            for (int j = 0; j <= 12; ++j)
                if (a[j] == 1) {
                    if (i & (1 << j))
                        lst.push_back((24 - j) % 24);
                    else
                        lst.push_back(j);
                }

            sort(ALL(lst));
            int res = 1e6;

            for (int j = 0; j != SZ(lst); ++j)
                res = min(res, (24 + lst[(j + 1) % SZ(lst)] - lst[j]) % 24);

            bst = max(bst, res);
        }

    return bst;
}

int main() {
    std::iostream::sync_with_stdio(false);
