bool solve() {
	int n; cin >> n;
	int d[n]; REP (i,n) cin >> d[i];
	sort(d,d+n);
	int time[n+1]; time[0] = 0;
	int gap = INF;
	REP (i,n) {
		int tmp[2];
		REP (j,2) {
			tmp[j] = INF;
			int hugo = pow(-1,j);
			int ntime = 24 + hugo * d[i]; ntime %= 24;
			REP (k,i+1) {
				int sa = abs(time[k]-ntime); if (sa > 12) sa = 24 - sa;
				tmp[j] = min(tmp[j],sa);
			}
		}
		if (tmp[0] < tmp[1]) {
			time[i+1] = 24-d[i];
		} else time[i+1] = d[i];
		gap = min(gap,max(tmp[0],tmp[1]));
	}
	// REP (i,n) cout << time[i] << endl;
	cout << gap << endl;
}

signed main() {
	cin.tie(0);
