using namespace std;
int ma=0;
int tim[13]={};
int n;
int mm=0;
void dfs(int depth,int s){
  if(depth==13||depth==mm+1){
    if(s!=INF)
      ma=max(s,ma);
    // cout<<ma<<endl;
    return;
  }
  int s2=s;
  for(int i=0;i<depth;i++){
    if(tim[i]>0){
      s=min(s,min(abs(depth-i),24-abs(depth-i)));
      s2=min(s2,min(abs((24-depth)-i),24-abs(24-depth-i)));
    }
  }
  //cout<<s<<" "<<s2<<endl;
  if(tim[depth]>=2){
    s=0;
    s2=min(s2,min(24-2*depth,2*depth));
  }
  dfs(depth+1,s);
  dfs(depth+1,s2);
}
int main(){
  cin>>n;
  for(int i=0;i<n;i++){
    int a;
    cin>>a;
    tim[a]++;
  }
  for(int i=12;i>=0;i--)
    if(tim[i]){
      mm=i;
      break;
    }
  if(n==1){
    ma=min(mm,24-mm);
  }else{
    dfs(0,INF);
  }
  cout<<abs(ma)<<endl;
  return 0;
}

