int main()
{
	int n,num[50],counts[25]={0},i,now,ans=0,tmp,x;
	cin >> n;
	if(n>25||n==1) 
	{
		puts("0");
		return 0;
	}
	for(i=0;i<n;i++)
	{
		cin >> num[i];
		counts[num[i]]++;
		if(counts[num[i]]==3)
		{
			puts("0");
			return 0;
		}
	}
	for(now=(1<<n)-1;now>=0;now--)
	{
		for(i=0;i<25;i++)
			counts[i]=0;
		for(i=0,tmp=now;i<n;tmp/=2,i++)
			if(tmp&1)
			{
				if(counts[24-num[i]]) break;
				else counts[24-num[i]]++;
			}
			else
			{
				if(counts[num[i]]) break;
				else counts[num[i]]++;
			}
		if(i<n) continue;
		for(i=0,tmp=-1,x=24;i<25 && x!=1;i++)
			if(counts[i])
			{
				if(tmp==-1) tmp=i;
				else x=min(x,i-tmp),tmp=i;
			}
		ans=max(x,ans);
	}
	cout << ans << "\n";
}

