inline void dfs(int q)
{
	if (q>m)
	{
		int xx=1<<30;
		for (int i=1;i<=cnt;i++) 
		{
			xx=min(c[i]-c[i-1],xx);
			if (a[c[i]]>1 && !d[24-c[i]])
			xx=0;
		}
		xx=min(c[cnt]-c[1],xx);
		xx=min(24-c[1],xx);
		xx=min(24-c[cnt],xx);
		if (xx!=1<<30)
		ans=max(xx,ans);
		return ;
	}
	int x=t[q];
	if (x>=12)
	  {
		if (a[24-x]==1 && d[24-x]) dfs(q+1);
		else {
			c[++cnt]=x;
			d[x]=1;
			dfs(q+1);
			d[x]=0;
			cnt--;
			if (d[24-x]==0) return ;
			if (x!=12 && a[24-x]!=1 && d[24-x]!=0){
			dfs(q+1);
		}
	  }
	  }
	else{
		c[++cnt]=x;
		d[x]=1;
		dfs(q+1);
		d[x]=0;
		cnt--;
