int d(int l, int r)
{
	if(l>r) swap(l,r);
	return min(r-l,24-(r-l));
}

int main()
{
	ios_base::sync_with_stdio(0); cin.tie(0);
	vector<int> ppl;
	int n; cin>>n;
	ppl.pb(0); 
	for(int i=0;i<n;i++)
	{
		int x; cin>>x; ppl.pb(x);
		if(x==0)
		{
			cout<<0<<'\n';
			return 0;
		}
	}
	sort(ppl.begin(),ppl.end());
	vector<int> v;
	for(int i=0;i<ppl.size();i++)
	{
		if(i>=2&&ppl[i]==ppl[i-1]&&ppl[i]==ppl[i-2])
		{
			cout<<0<<'\n';
			return 0;
		}
		else
		{
			v.pb(ppl[i]);
		}
	}
	n=v.size();
	assert(n<=25);
	int ans=0;
	for(int i=0;i<(1<<n);i++)
	{
		bool pos=1;
		for(int j=0;j<n;j++)
		{
			if(i&(1<<j))
			{
				a[j]=v[j];
			}
			else
			{
				a[j]=(24-v[j])%24;
			}
			if(j>0&&d(a[j],a[j-1])<=ans)
			{
				pos=0; break;
			}
		}
		if(!pos) continue;
		sort(a,a+n);
		int res=1000;
		for(int j=0;j<n;j++)
		{
			res=min(res,d(a[j],a[(j+1)%n]));
			if(res<=ans) break;
		}
		ans=max(ans,res);
	}
	cout<<ans<<'\n';
}

