int main(){
    int N; cin >> N;
    vector<int> D(N);
    for(int i=0;i<N;i++) cin >> D[i];

    vector<int> cnt(13,0);
    cnt[0] += 1;
    for(int i=0;i<N;i++) cnt[D[i]]++;

    int ans = -1;

    for(int i=0;i<(1<<11);i++){
        vector<bool> used(24,false);
        int s = numeric_limits<int>::max();

        for(int j=0;j<=12;j++){
            if(cnt[j]){
                if(j==0 || j==12){
                    used[j] = true;
                    if(cnt[j]>=2){
                        s = 0;
                    }
                }else{
                    if(cnt[j] == 1){
                        used[((i & (1<<(j-1))) ? j : 24-j)] = true;
                    }else if(cnt[j] == 2){
                        used[j] = used[24-j] = true;
                    }else{
                        s = 0;
                    }
                }
            }
        }
        for(int j=0;j<24;j++){
            if(used[j]){
                for(int k=0;k<j;k++){
                    if(used[k]){
                        s = min(s, min(abs(k-j), 24 - abs(k-j)));
                    }
                }
            }
        }
        ans = max(ans, s);
    }

    cout << ans << endl;
    return 0;
}

