template<class T> inline bool chmax(T& a, T b) { if (a < b) { a = b; return 1; } return 0; }
template<class T> inline bool chmin(T& a, T b) { if (a > b) { a = b; return 1; } return 0; }
using namespace std;

signed main(void){
    cin.tie(0); cout.tie(0);
    ios::sync_with_stdio(false);

    int n; cin>>n;
    vector<int> d(n);
    rep(i,n)cin>>d[i];

    if(n>=24){
        cout<<0<<endl;
        return 0;
    }

    int ans=0;
    for(int bit=0; bit<(1<<n); bit++){
        bitset<24> st;
        int tmp=30;
        rep(i,n){
            if(d[i]==0){
                tmp=0; break;
            }
            if((1<<i)&bit){
                if(st[d[i]]){
                    tmp=0; break;
                }
                st[d[i]]=true;
            }else{
                if(st[24-d[i]]){
                    tmp=0; break;
                }
                st[24-d[i]]=true;
            }
        }
        int cur=0;
        rep(i,24){
            if(st[i]){
                chmin(tmp,i-cur);
                chmin(tmp,24-i);
                cur=i;
            }
        }
        chmax(ans,tmp);
    }
    cout<<ans<<endl;

}

