using namespace std;
priority_queue<int,vector<int>,greater<int> > que;
int main(){
  int N,D[50];
  scanf("%d",&N);
  if(N==1){
    printf("0\n");return 0;
  }
  ROP(i,N){
    scanf(" %d",&D[i]);
    if(D[i]!=0) que.push(D[i]);
  }
  ROP(i,N){
    ROP(j,N){
      if(i<j){
        if(D[i]!=0||D[j]!=0){
          int MIN,MMIN,MAX;
          int lar=max(D[i],D[j]),sma=min(D[i],D[j]);
          MIN=min(lar-sma,24-(lar-sma));
          MMIN=min((24-lar)-sma,24-((24-lar)-sma));
          MAX=max(MIN,MMIN);
          que.push(MAX);
        }else{
          que.push(24);
        }
      }
    }
  }
  printf("%d\n",que.top());
  return 0;
}

