const ll MOD=1000000007LL;

int shortest(ll flag) {
    ll wflag = flag | (flag << 24); 
    int dmin = 24;
    for (ll i=0,b=1; i<24; ++i,b<<=1LL) {
        if (!(flag & b)) continue;
        int d = 1;
        for (ll j=i+1,m=b*2; j<i+24; ++j,m<<=1LL) {
            if (flag & m) break;
            ++d;
        }
        dmin = min(dmin, d);
    }
    return dmin;
}

ll solve(int N, vi& d) {
    vi st(13, 0);
    st[0] = 1;
    for(int di:d) st[di]++;

    ll flag = 0;
    if (st[0] >= 2) return 0;
    if (st[0]) flag |= (1 << 0);    

    if (st[12] >= 2) return 0;
    if (st[12]) flag |= (1 << 12); 

    vi ones;
    for (int i=1; i<=11; ++i) {
        if (st[i] >= 3) return 0;
        if (st[i] == 2) {
            flag |= (1 << i);
            flag |= (1 << (24-i));
        } else if (st[i] == 1) {
            ones.pb(i);
        }
    }
    int W = ones.size();
    if (W == 0) {
        return shortest(flag);
    }
    int P = 1 << W;
    int best = 0;
    rep(p,P) {
        ll flag_ = flag;
        for (int i=0,m=1; i<W; ++i,m<<=1) {
            if (p & m) {
                flag_ |= (1 << ones[i]);
            } else {
                flag_ |= (1 << (24 - ones[i]));
            }
        }
        int d_ = shortest(flag_);
        best = max(best, d_);
    }
    return best;
}

int main() {
    int N; cin >> N;
    vi d(N); rep(i,N) cin >> d[i];
    cout << solve(N,d) << endl;
    return 0;
}

