#define ROP(i,n) for(int i=0;i<n;i++)
using namespace std;
priority_queue<int> que;
int main(){
  int N,D[50],DD[50],cnt=0;
  scanf("%d",&N);
  ROP(i,N){
    scanf(" %d",&D[i]);
    DD[i]=D[i];
  }
  if(N==1){
    printf("0\n");return 0;
  }
  ROP(i,N){
    if(D[i]==12) cnt++;
    else if(cnt==2){
      printf("0\n");return 0;
    }else if(D[i]==0){
      printf("0\n");return 0;
    }
  }
  ROP(i,N){
    int MIN=12,cnt=0;DD[i]=24-D[i];
    ROP(i,N){
      MIN=min(MIN,D[i]);
    }
    ROP(j,N){
      if(i==j) continue;
      MIN=min(MIN,abs(DD[i]-D[j]));
    }
    DD[i]=D[i];
    ROP(j,N){
      if(i==j) continue;
      if(DD[i]==D[j]){
        if(++cnt==2){
          printf("0\n");
          return 0;
        }
        continue;
      }
      MIN=min(MIN,abs(DD[i]-D[j]));
    }
    que.push(MIN);
  }
  printf("%d\n",que.top());
  return 0;
}

