static int ct[MAXD + 1];

static bool sel[MAXT] = { false };

int main()
{
    scanf("%d", &n);
    for (int i = 0; i < n; ++i) {
        scanf("%d", &d[i]);
        ++ct[d[i]];
    }

    if (ct[0] >= 1 || ct[MAXD] >= 2) { puts("0"); return 0; }
    for (int i = 1; i < MAXD; ++i) if (ct[i] >= 3) { puts("0"); return 0; }

    int base_mask = 1 | (ct[MAXD] ? (1 << MAXD) : 0);
    for (int ans = 12; ans > 0; --ans) {
        bool valid;
        for (int i = 0; i < (1 << (MAXD - 1)); ++i) {
            valid = true;
            int mask = base_mask, time;
            for (int j = 1; j < MAXD; ++j) switch (ct[j]) {
                case 0: default: break;
                case 1:
                    time = (i & (1 << (j - 1))) ? j : MAXT - j;
                    if (mask & (1 << time)) { valid = false; break; }
                    mask |= (1 << time);
                    break;
                case 2:
                    if (mask & (1 << j)) { valid = false; break; }
                    if (mask & (1 << (MAXT - j))) { valid = false; break; }
                    mask |= (1 << j) | (1 << (MAXT - j));
                    break;
            }
            if (!valid) continue;
            for (int j = 0; j < MAXT; ++j) sel[j] = (mask & (1 << j));
            for (int j = 0; j < MAXT; ++j) if (sel[j]) {
                for (int k = 1; k < ans; ++k) {
                    if (sel[(j - k + MAXT) % MAXT] || sel[(j + k) % MAXT]) {
                        valid = false; break;
                    }
                }
            }
            if (valid) break;
        }
        if (valid) { printf("%d\n", ans); return 0; }
    }

    puts("0"); return 0;
}

