int diff(ll mask) {
  int res = 12;
  rep(i, 25) {
    if (!(mask & (1LL << i))) continue;
    for (int j = i + 1; j < 25; j++) {
      if (!(mask & (1LL << j))) continue;
      int d = (i - j + 24) % 24;
      res = min(res, min(d, 24 - d));
    }
  }
  //printf("%d, %llx\n", res, mask);
  return res;
}

int dfs(int i, ll mask) {
  int res = 0;
  if (i > v.size() - 1) return diff(mask);

  if (!(mask & (1LL << v[i]))) {
    res = max(res, dfs(i + 1, mask | (1LL << v[i])));
  }
  if (!(mask & (1LL << (24 - v[i])))) {
    res = max(res, dfs(i + 1, mask | (1LL << (24 - v[i]))));
  }
  return res;
}

