	REP(f, n) FOR(s, f+1, n){
		int p1 = abs(a[f] - a[s]);
		if(min(p1, 24 - p1) < x){
			tree.unite(f, s+n);
			tree.unite(f+n, s);
			if(tree.same(f, f+n)) return false;
			if(tree.same(s, s+n)) return false;
		}
		int p2 = abs(a[f] + a[s] - 24);
		if(min(p2, 24 - p2) < x){
			tree.unite(f, s);
			tree.unite(f+n, s+n);
			if(tree.same(f, f+n)) return false;
			if(tree.same(s, s+n)) return false;
		}
	}
	return true;
}

