void dfs(vector<int> dd, int id, int n){
	if (id == n){
		int tempmin = 24;
		for (int i = 0; i < dd.size(); i++){
			for (int j = 0; j < i; j++){
				tempmin = min(tempmin, min(24-abs(dd[i]-dd[j]), abs(dd[i]-dd[j])));
			}
		}
		m = max(m, tempmin);
	}
	else {
		dd.push_back(d[id]);
		dfs(dd, id+1, n);
		dd.pop_back();
		dd.push_back(-d[id]);
		dfs(dd, id+1, n);
		dd.pop_back();
	}
}


