int N;
int D[55];
int T[55];

int solve() {
  if (N >= 24) return 0;

  int res = 0;
  for (int bit = 0; bit < (1<<N); ++bit) {
	if (bit % 2 == 0) continue;
	int tmp = 30;
	T[0] = 0;
	for (int i = 0; i < N; ++i) {
	  if (bit & (1<<i)) T[i+1] = D[i];
	  else T[i+1] = 24 - D[i];
	}
	sort(T, T+N+1);
	for (int i = 0; i < N+1; ++i) {
	  int j = (i+1) % (N+1);
	  int diff = min( abs(T[j] - T[i]), 24 - abs(T[j] - T[i]) );
	  chmin(tmp, diff);
	}
	chmax(res, tmp);
  }
  return res;
}

